import React, {useState} from 'react'

export default function TextForm(props) {

    const [text,setText] = useState("");
    // text = "hdshjg"; wrong way to change the vlaue of text
    // setText("here"); correct way to change the text;

    const  handleUpChange = () =>{
        console.log("Uppercase is clicked "+text);
        let upperText = text.toUpperCase();
        setText(upperText);
    }
    const handleOnChange = (event) => {
        console.log("onChanged");
        setText(event.target.value);
    }

    const handleLoChange = () => {
        let lowerText = text.toLowerCase();
        setText(lowerText);
    }

    const handleClearText = () => {
        let clearText = text.split('');
        setText(clearText);
    }
    return (
        <>
        <div className='container'>
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
            </div>
            <div className="button btn btn btn-primary mx-2" onClick={handleUpChange}>Convert To Uppercase</div>
            <div className="button btn btn btn-primary mx-2" onClick={handleLoChange}>Convert To LowerCase</div>
            <div className="button btn btn btn-primary mx-2" onClick={handleClearText}>Clear Text</div>

        </div>

        <div className="container my-3">
            <h2>Your Text Summary</h2>
            <p>{text.split("").length}words and {text.length} characters</p>
            <p>{0.008 * text.split("").length} minutes to read</p>
            <h2>Preview</h2>
            <p>{text}</p>
        </div>

        </>
    )
}
